package com.lonfe.config;import org.apache.commons.logging.Log;import org.apache.commons.logging.LogFactory;import org.apache.ibatis.session.SqlSessionFactory;import org.mybatis.spring.SqlSessionFactoryBean;import org.mybatis.spring.annotation.MapperScan;import org.springframework.boot.autoconfigure.SpringBootApplication;import org.springframework.context.annotation.Bean;import org.springframework.core.io.support.PathMatchingResourcePatternResolver;import org.springframework.core.io.support.ResourcePatternResolver;import javax.annotation.Resource;import javax.sql.DataSource;@SpringBootApplication@MapperScan("com.lonfe.dao")public class MybatisConfiguration {	private static Log logger = LogFactory.getLog(MybatisConfiguration.class);	@Resource(name="ds")	private DataSource ds;	@Bean	public SqlSessionFactory sqlSessionFactory() throws Exception{			ResourcePatternResolver resolver = new PathMatchingResourcePatternResolver();			SqlSessionFactoryBean sessionFactory = new SqlSessionFactoryBean();			sessionFactory.setDataSource(ds);			sessionFactory.setTypeAliasesPackage("com.lonfe.entity");			sessionFactory					.setMapperLocations(resolver.getResources("classpath:/mapper/*.xml"));				return sessionFactory.getObject();	}}