package com.lonfe.controller;import com.lonfe.dao.UserMapper;import com.lonfe.entity.User;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestMethod;import org.springframework.web.bind.annotation.RequestParam;import org.springframework.web.bind.annotation.RestController;import java.util.List;@RestControllerpublic class HelloController {	@Autowired	private UserMapper userDao;	@RequestMapping(value = "/hello", method = RequestMethod.GET)	public String hello () {		return "hello world!";	}	@RequestMapping(value = "/getUserList", method = RequestMethod.GET)	public List<User> getUserList() {		return userDao.findAll();	}	@RequestMapping(value = "/getUserOne", method = RequestMethod.GET)	public User getUserOne(@RequestParam(value = "id") Integer id) {		return userDao.findOne(id);	}}