package com.lonfe.feign;import com.lonfe.entity.User;import org.springframework.cloud.netflix.feign.FeignClient;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestMethod;import org.springframework.web.bind.annotation.RequestParam;@FeignClient(value = "computer-service", fallback = UserFeignHystrix.class)public interface UserFeign {	@RequestMapping(value = "/getUserOne", method = RequestMethod.GET)	User getUserOne(@RequestParam("id") Integer id);}