package com.lonfe;import org.springframework.boot.autoconfigure.SpringBootApplication;import org.springframework.boot.builder.SpringApplicationBuilder;import org.springframework.cloud.netflix.eureka.server.EnableEurekaServer;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestMethod;import org.springframework.web.bind.annotation.RestController;@EnableEurekaServer@SpringBootApplication@RestControllerpublic class Application {	public static void main(String[] args) {		new SpringApplicationBuilder(Application.class).web(true).run(args);	}	@RequestMapping(value = "/hello", method = RequestMethod.GET)	public String hello () {		return "hello world!";	}}